const accordion = document.getElementsByClassName("contentBox");

for(var i=0; i<accordion.length; i++) {
  accordion[i].addEventListener('click', function() {

    this.classList.toggle('active');
  });

  if (accordion[i] !== this) {
    accordion[i].classList.remove('active');
  };

};

